import React, { useState, useEffect} from "react";
import { Switch, Route, Link, BrowserRouter as Router, userParams  } from "react-router-dom";

// function SongCard({ song, isFav, addFav }) {
function SongCard({ song, favs, set }) {
    // const { id } = song;
    const { id } = useParams();
    const [individualSong, setIndividualSong] = useState([]);

    // const{song_id} = song
    const [isFavorited, setIsFavorited] = useState(false);
    const current_user_id = 1;
    // const createFavorite = () => {
    //     const newFav = {
    //         user_id: 1, song_id, note: ""
    //     }
    //     fetch("http://localhost:3000/favorite_songs", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify(newFav),
    // })
    //   .then((r) => r.json())
    //   .then((data) => {
    //     console.log(data)
    //   });
    // }

    console.log("this is the individual song", song)

    function toggleFav(e) {
        setIsFavorited(!isFavorited);
        handleSubmit(e);
    }

    function handleSubmit(e) {
        e.preventDefault();
        fetch("http://localhost:3000/favorite_songs", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ user_id: current_user_id, song_id: song.id }),
        })
            .then((r) => r.json())
            .then((data) => {
                const newFav = [...favs, data];
                set(newFav);
            });

    }

    useEffect(() => {
        fetch(`http://localhost:3000/songs/${song.id}`)
            .then((r) => r.json())
            .then((song) => setIndividualSong(song));
    }, []);

    return (
        <div className="col-md-4">
            <div className="card">
                <img
                    className="card-img-top"
                    src={song.image}
                />
                <div className="card-body">
                    <h4 class="card-title">
                        <Link song={song} to={`/songs/${song.id}`}>{song.name}</Link>
                    </h4>
                    <p className="card-text">
                        Range: {song.lowest_note}-{song.highest_note}
                    </p>   
                </div>
                {/* <div
                        // href="#"
                        className="btn btn-outline-secondary"
                        onClick={() => {
                            setIsFavorited(!isFavorited)
                            createFavorite()
                        }}
                        // className="emoji-button favorite active"
                    >
                        {isFavorited ? "★" : "☆"}
                </div> */}
                {isFavorited ? (
                    <button
                        // href="#"
                        className="btn btn-outline-secondary"
                        onClick={toggleFav}
                        // className="emoji-button favorite active"
                    >
                        ★
                    </button>
                    ) : (
                    <button
                        // href="#"
                        className="btn btn-outline-secondary"
                        onClick={toggleFav}
                        // className="emoji-button favorite"
                    >
                        ☆
                    </button>
                    )}
            </div>
        </div>
    );
  }

  export default SongCard;
