import React, { useState } from "react";
import SongCard from "./SongCard.js";

// function Home({ addFav, userFavs, songs, userId }) {
function Home({ songs, fav_songs, set_fav_songs }) {
    const [year, setYear] = useState(null);
    const [name, setName] = useState(null);
    const [lowPitch, setLowPitch] = useState(null);
    const [highPitch, setHighPitch] = useState(null);

    // const userFavIds = userFavs.map(f => f.song_id)

    let songsToDisplay = [...songs]
    if(year) {
        songsToDisplay = songsToDisplay.filter(song => {
            return song.year === year
        })
    }
    if(name) {
        songsToDisplay = songsToDisplay.filter(song => {
            return song.name.toLowerCase().includes(name.toLowerCase())
        })
    }
    if(lowPitch) {
      songsToDisplay = songsToDisplay.filter(song => {
        return song.lowest_pitch >= parseInt(lowPitch)
    })
    }
    if(highPitch) {
      songsToDisplay = songsToDisplay.filter(song => {
        return song.highest_pitch <= parseInt(highPitch)
    })
    }
  return (
    <div className="container-fluid padding">
      <div class="jumbotron jumbotron-fluid">
        <p class="lead">Find the perfect song for your needs using these filters:</p>
        <label for="songNameInput">Song Name</label>
        <input class="form-control filter" id="songNameInput"placeholder="Type a song name..." onChange={(e) => {
            const val=e.target.value
            setName(val)
        }}/>
        {/* <div class="dropdown">
          <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Year
          </button> */}
          <select class="dropdown-menu" aria-labelledby="dropdownMenuButton" onChange={(e) => {
            const val=e.target.value
            setYear(parseInt(val))
          }}>
            <option class="dropdown-item" href="#" value="2020">2020</option>
            <option class="dropdown-item" href="#" value="2019">2019</option>
          </select>
        {/* </div> */}
        {/* <select class="form-select" aria-label="Default select example" onChange={(e) => {
            const val=e.target.value
            setYear(parseInt(val))
        }}>
            <option selected value="">Year</option>
            <option value="2020">2020</option>
            <option value="2019">2019</option>
            <option value="2018">2018</option>
            <option value="2017">2017</option>
            <option value="2016">2016</option>
        </select> */}
        <input onChange={(e) => {
          setLowPitch(e.target.value)
        }} />
        <input onChange={(e) => {
          setHighPitch(e.target.value)
        }} />


      </div>
      <div className="row padding">
        {songsToDisplay.map((song) => {
            // const isFav = userFavIds.includes(song.id)
          // return <SongCard addFav={addFav} key={song.id} song={song} isFav={isFav} />;
          return <SongCard key={song.id} song={song} favs={fav_songs} set={set_fav_songs} />;
        })}
      </div>
    </div>
  );
}

export default Home;
